mapper.update:
UPDATE tb_business SET business_name = #{businessName},
CEO_name = #{CEOname}, business_address = #{businessAddress},
business_address_detail = #{businessAddressDetail}, business_number = #{businessNumber},
business_registration_img = #{businessRegistrationNumberImg}, business_registration_number = #{businessRegistrationNumber}, state = #{state}
WHERE business_id = #{businessId};

UPDATE tb_business SET business_name = #{businessName},
CEO_name = #{CEOname}, business_address = #{businessAddress},
business_address_detail = #{businessAddressDetail}, business_number = #{businessNumber}
WHERE user_id = #{userId};

mapper.select:
SELECT ls.lecture_session_id, ls.session_date, l.title, l.price, c.category_name, (SELECT COUNT(r.reserve_id) FROM tb_reserve r JOIN tb_payment p ON r.reserve_id = p.reserve_id WHERE ls.lecture_session_id = r.lecture_session_id AND p.state = '결제 완료') AS student, l.maximum, l.state, ls.created_at FROM tb_lecture_session ls JOIN tb_lecture l ON ls.lecture_id = l.lecture_id JOIN tb_category c ON l.category_id = c.category_id WHERE 1=1 AND l.user_id = #{userId} AND DATE_FORMAT(session_date, "%Y-%m-%d") >= CURDATE()
and l.title like CONCAT('%',#{keyword},'%')
ORDER BY ls.session_date LIMIT #{startNum}, 10;

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_lecture_session ls JOIN tb_lecture l ON ls.lecture_id = l.lecture_id JOIN tb_category c ON l.category_id = c.category_id WHERE 1=1 AND l.user_id = #{userId} AND DATE_FORMAT(session_date, "%Y-%m-%d") >= CURDATE()
and p.person_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT ls.lecture_session_id, ls.session_date, l.title, l.price, c.category_name, (SELECT COUNT(r.reserve_id) FROM tb_reserve r JOIN tb_payment p ON r.reserve_id = p.reserve_id WHERE ls.lecture_session_id = r.lecture_session_id AND p.state = '결제 완료') AS student, l.maximum, l.state, ls.created_at FROM tb_lecture_session ls JOIN tb_lecture l ON ls.lecture_id = l.lecture_id JOIN tb_category c ON l.category_id = c.category_id WHERE 1=1 AND l.user_id = #{userId} AND DATE_FORMAT(session_date, "%Y-%m-%d") < CURDATE()
and l.title like CONCAT('%',#{keyword},'%')
ORDER BY ls.session_date LIMIT #{startNum}, 10;

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_lecture_session ls JOIN tb_lecture l ON ls.lecture_id = l.lecture_id JOIN tb_category c ON l.category_id = c.category_id WHERE 1=1 AND l.user_id = #{userId} AND DATE_FORMAT(session_date, "%Y-%m-%d") < CURDATE()
and p.person_name like CONCAT('%',#{keyword},'%')
) ost;

mapper.update:
UPDATE tb_business SET business_name = #{businessName},
CEO_name = #{CEOname}, business_address = #{businessAddress},
business_address_detail = #{businessAddressDetail}, business_number = #{businessNumber}
WHERE user_id = #{userId};

mapper.select:
SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND b.state = '등록요청'
and business_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT IF(substr(username,length(username)-5)='_kakao', '카카오 로그인', username ) as username, b.* FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND b.state = '등록요청'
and business_name like CONCAT('%',#{keyword},'%')
ORDER BY created_at DESC LIMIT #{startNum}, 10;

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND u.identity = 'business'
and business_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT IF(substr(username,length(username)-5)='_kakao', '카카오 로그인', username ) as username, b.* FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND u.identity = 'business'
and business_name like CONCAT('%',#{keyword},'%')
ORDER BY created_at DESC LIMIT #{startNum}, 10;
