mapper.update:
UPDATE tb_business SET business_name = #{businessName},
CEO_name = #{CEOname}, business_address = #{businessAddress},
business_address_detail = #{businessAddressDetail}, business_number = #{businessNumber},
business_registration_img = #{businessRegistrationNumberImg}, business_registration_number = #{businessRegistrationNumber}, state = #{state}
WHERE business_id = #{businessId};

UPDATE tb_business SET business_name = #{businessName},
CEO_name = #{CEOname}, business_address = #{businessAddress},
business_address_detail = #{businessAddressDetail}, business_number = #{businessNumber}
WHERE user_id = #{userId};

mapper.select:
SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND b.state = '등록요청'
and business_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT u.username, b.* FROM tb_business b LEFT JOIN tb_user u ON b.user_id = u.user_id WHERE 1=1 AND b.state = '등록요청'
and business_name like CONCAT('%',#{keyword},'%')
ORDER BY created_at DESC LIMIT #{startNum}, 10;
