mapper.insert:
		INSERT INTO tb_lecture(category_id,user_id, title,content, address, address_detail, latitude, longitude, maximum, price, phone_number, state, created_at, duration)
		values(#{categoryId},#{userId},#{title},#{content},#{address},#{addressDetail},#{latitude},#{longitude},#{maximum},#{price},#{phoneNumber},#{state},now(),#{duration});


       		 SELECT LAST_INSERT_ID()

mapper.update:
UPDATE tb_lecture SET title = #{title}, content = #{content},
address = #{address}, address_detail = #{addressDetail},
latitude = #{latitude}, longitude = #{longitude},
maximum = #{maximum}, price = #{price},	phone_number = #{phoneNumber},
state = #{state}, duration = #{duration}
WHERE lecture_id = #{lectureId};

mapper.select:
SELECT count(*) FROM tb_lecture l JOIN tb_category c ON l.category_id = c.category_id JOIN tb_user u ON l.user_id = u.user_id WHERE l.state = 1
AND REPLACE(l.title, ' ', '') LIKE '%${title}%'
AND c.category_name = #{categoryName}
AND l.price >= #{minPrice}
AND #{maxPrice} >= l.price
AND address LIKE '${location}%'

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_lecture l WHERE 1=1
and lecture_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT l.lecture_id, l.title, l.user_id, b.business_name, l.address, l.address_detail, l.price, l.price, c.category_name, l.state,l.created_at FROM tb_lecture l LEFT JOIN tb_category c ON c.category_id = l.category_id LEFT JOIN tb_business b ON l.user_id = b.user_id WHERE 1=1
and lecture_name like CONCAT('%',#{keyword},'%')
ORDER BY l.created_at DESC LIMIT #{startNum}, 10;
