mapper.select:
SELECT r.reserve_id, p.state, p.payment_date, p.amount, lp.img, l.title, ls.session_date FROM tb_reserve r JOIN tb_payment p ON r.reserve_id = p.reserve_id LEFT JOIN tb_lecture_session ls ON r.lecture_session_id = ls.lecture_session_id LEFT JOIN tb_lecture l ON ls.lecture_id = l.lecture_id LEFT JOIN tb_lecture_photo lp ON ls.lecture_id = lp.lecture_id WHERE r.user_id = #{userId}
and l.title like CONCAT('%',#{keyword},'%')
and qt.state like CONCAT('%',#{status},'%')
ORDER BY reserve_id DESC LIMIT #{startNum}, 10;

SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_reserve r LEFT JOIN tb_lecture_session ls ON r.lecture_session_id = ls.lecture_session_id LEFT JOIN tb_lecture l ON ls.lecture_id = l.lecture_id WHERE r.user_id = #{userId}
and l.title like CONCAT('%',#{keyword},'%')
and qt.state like CONCAT('%',#{status},'%')
) ost;
