mapper.insert:
INSERT INTO tb_user(username, password, identity, created_at)
values(#{username},#{password},#{identity},now());

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE tb_user SET username = #{username},identity = #{identity},
password = #{password}
WHERE user_id = #{userId};

mapper.select:
SELECT totalCount, totalPage, currentPage, CASE WHEN currentPage = 1 THEN 1 ELSE 0 END AS isFirst, CASE WHEN currentPage = totalPage THEN 1 ELSE 0 END AS isLast FROM( SELECT count(*) totalCount, ceil(count(*)/10) totalPage,#{page} currentPage, 0 isFirst, 0 isLast FROM tb_person p LEFT JOIN tb_user u ON p.user_id = u.user_id WHERE 1=1 AND u.identity = 'customer'
and p.person_name like CONCAT('%',#{keyword},'%')
) ost;

SELECT u.username, p.* FROM tb_person p LEFT JOIN tb_user u ON p.user_id = u.user_id WHERE 1=1 AND u.identity = 'customer'
and p.person_name like CONCAT('%',#{keyword},'%')
ORDER BY created_at DESC LIMIT #{startNum}, 10;
